import React, { Component } from 'react';
import FormRegis from '../component/register/FormRegis';
import Headnav from '../component/template/navigasi/Headnav';
import Navbar from '../component/template/navigasi/Navbar';
import axios from 'axios';

class VRegister extends Component {

    state = {
        user:{
          username : '',
          email : '',
          password : '',
          alamat : '',
          noTelp : '',
          redirect: false
        }
      }

      handleTambahUser = (e) => {
        let val = e.target.value;
        let name_val = e.target.name;
        let dataUser = this.state.user;

        // console.log(name_val);
        console.log(val);
        dataUser[name_val] = val;
        this.setState({
          user : dataUser
        }, () => {
          // console.log(this.state.user);
        })
      }

      handleSubmit = (e) => {
        e.preventDefault();
        this.postDataToApi();
        console.log(this.state.user);
      }

      postDataToApi = () => {
        axios.post('http://localhost:3004/posts', this.state.user).then((res) => {
          console.log(res);
          this.setState({ redirect: true })
        }, (err) => {
          console.log(err);
        })
      }

  render() {
    return (
    	<div>
      <Headnav />
            <Navbar />
      <div className="col-md-4 offset-md-4 mt-5 card pt-5 pb-5">
      	<FormRegis 
          inputData={this.handleTambahUser.bind(this)} 
          submit={this.handleSubmit.bind(this)} 
        />
      </div>
      </div>
    );
  }
}

export default VRegister;
