import React, { Component } from 'react';
import FormRegis from '../component/register/FormRegis';
import Headnav from '../component/template/navigasi/Headnav';
import Navbar from '../component/template/navigasi/Navbar';

class VRegister extends Component {

    state = {
        user:{
          nama_produk : '',
          harga_produk : '',
          image_produk : '',
          redirect: false
        }
      }

      handleTambahProduk = (e) => {
        let val = e.target.value;
        let name_val = e.target.name;
        let dataProduk = this.state.produk;
        let timeNow = new Date().getTime();

        dataProduk[name_val] = val;
        dataProduk['id'] = timeNow;
        this.setState({
          produk : dataProduk
        }, () => {
          console.log(dataProduk);
        })
      }

      handleSubmit = (e) => {
        e.preventDefault();
        this.postDataToApi();
        console.log(this.state.produk);
      }

      postDataToApi = () => {
        axios.post('http://localhost:3004/posts', this.state.produk).then((res) => {
          console.log(res);
          this.setState({ redirect: true })
        }, (err) => {
          console.log(err);
        })
      }

  render() {
    return (
    	<div>
      <Headnav />
            <Navbar />
      <div className="col-md-4 offset-md-4 mt-5 card pt-5 pb-5">
      	<FormRegis />
      </div>
      </div>
    );
  }
}

export default VRegister;
